sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {

    if (isLoading || newValue === '' || newValue == oldValue) {
        return;
    }

    var gaPhone = new GlideAjax('userDetailsAJAX');
    gaPhone.addParam('sysparm_name', 'getEmail');
    gaPhone.addParam('sysparm_userID', g_form.getValue('note_proposed_by'));
    gaPhone.getXMLAnswer(populateEmailField);

	function populateEmailField(response) {
		var emailFromScriptInclude = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('note_proposed_by_email');
		g_form.setValue('note_proposed_by_email',emailFromScriptInclude);
	}

}
